mapper.select:
SELECT COUNT(*)
FROM COVERLETTER
WHERE CVCATEGORY = #{cvcategory}
AND JOINEMAIL = #{joinemail}


		AND TITLE LIKE ('%' || #{keyword} || '%')


		AND CONTENT LIKE ('%' || #{keyword} || '%')

SELECT B.*
FROM (
SELECT A.*, ROW_NUMBER() OVER(ORDER BY COVERLETTERSEQ DESC) AS RNUM
FROM COVERLETTER A
WHERE CVCATEGORY = #{cvcategory}
AND JOINEMAIL = #{joinemail}


		AND TITLE LIKE ('%' || #{keyword} || '%')


		AND CONTENT LIKE ('%' || #{keyword} || '%')


) B
WHERE RNUM BETWEEN #{StartIndex} + 1 AND #{CntPerPage}
ORDER BY COVERLETTERSEQ DESC
